import React, {Component} from "react";
import img from './img/forest0.jpg'
class Slider extends Component {
    constructor(props){
        super(props)
        this.state = {
            images: [
                './img/forest0.jpg'
            ],
            currentImagesIndex: 0,
            isCycleMode: false
        }
    }

    nextSlideHandler = () => {
        this.setState({currentImagesIndex: this.state.currentImagesIndex + 1})
    }
    prevSlideHandler = () => {
        this.setState({currentImagesIndex: this.state.currentImagesIndex - 1})
    }

    render() {
        return(
            <div className="slider">
                <div>
                    <button onClick={this.prevSlideHandler}>Prev</button>
                </div>
                <div>
                    <img src='img/forest0.jpg' alt="slider"/>
                    {this.state.currentImagesIndex}
                </div>
                    <button onClick={this.nextSlideHandler}>Next</button>
            </div>
        )
    }

}

export default Slider;
