import React, {Component} from "react";
import Photo0 from './img/forest0.jpg'
import Photo1 from './img/forest1.jpg'
import Photo2 from './img/forest2.jpg'
import Photo3 from './img/forest3.jpg'
import Photo4 from './img/forest4.jpg'
import Photo5 from './img/forest5.jpg'
import Photo6 from './img/forest6.jpg'
import Photo7 from './img/forest7.jpg'
import Photo8 from './img/forest8.jpg'
import style from './Slider.module.css'
class Slider extends Component {
    constructor(props){
        super(props)
        this.state = {
            images: [
                Photo0,
                Photo1,
                Photo2,
                Photo3,
                Photo4,
                Photo5,
                Photo6,
                Photo7,
                Photo8
            ],
            currentImagesIndex: 0,
            isCycleMode: false
        }
    }

    nextSlideHandler = () => {
        if (this.state.currentImagesIndex < this.state.images.length - 1) {
            this.setState({currentImagesIndex: this.state.currentImagesIndex + 1})
        }
    }
    prevSlideHandler = () => {
        if (this.state.currentImagesIndex > 0) {
            this.setState({currentImagesIndex: this.state.currentImagesIndex - 1})
        }
    }
    deleteHandler = () => {
        let arr = this.state.images
        arr.splice(this.state.currentImagesIndex, 1)
        this.setState({images: arr})
    }

    render() {
        return(
            <div className={style.slider}>
                <div>
                    <button onClick={this.prevSlideHandler}>Prev</button>
                </div>
                <div className={style.container}>
                    <img className={style.preview} src={this.state.images[this.state.currentImagesIndex - 1]}/>
                    <img src={this.state.images[this.state.currentImagesIndex]} alt="slider"/>
                    <img className={style.preview} src={this.state.images[this.state.currentImagesIndex + 1]}/>
                    <button onClick={this.deleteHandler}>Delete</button>
                </div>
                <div>
                    <button onClick={this.nextSlideHandler}>Next</button>
                </div>
            </div>
        )
    }

}

export default Slider;
